from Crypto.Util.number import isPrime

flag = open("./flag.txt").read()

primes = []
last = 1
while len(primes) < len(flag):
    if isPrime(last):
        primes.append(last)
    last += 1

N = 1
lp = 0

for char in flag:
    N *= pow(primes[lp], ord(char))
    lp += 1

print(N) # 